svg.script:
    // Globalne zmienne
    let currentFilter = 'all';
    let data = [];

    // Inicjalizacja po za≈Çadowaniu SVG
    function init() {
      // Wczytaj dane z metadanych
      const dataStore = document.querySelector('metadata data-store');
      if (dataStore) {
        const jsonData = JSON.parse(dataStore.textContent);
        data = jsonData.data;
      }

      // Dodaj event listenery
      setupEventListeners();
      updateChart();
    }

    // Konfiguracja event listener√≥w
    function setupEventListeners() {
      // Przyciski filtrowania
      const buttons = document.querySelectorAll('.button[data-region]');
      buttons.forEach(button => {
        button.addEventListener('click', function() {
          currentFilter = this.getAttribute('data-region');
          updateChart();
          highlightButton(this);
        });
      });

      // Przycisk eksportu
      const exportBtn = document.getElementById('exportBtn');
      if (exportBtn) {
        exportBtn.addEventListener('click', exportData);
      }

      // Przycisk info
      const infoBtn = document.getElementById('infoBtn');
      if (infoBtn) {
        infoBtn.addEventListener('click', showInfo);
      }

      // Interakcja ze s≈Çupkami
      const bars = document.querySelectorAll('.bar');
      bars.forEach(bar => {
        bar.addEventListener('mouseenter', function() {
          this.setAttribute('opacity', '1');
          this.setAttribute('stroke', '#333');
          this.setAttribute('stroke-width', '2');
        });

        bar.addEventListener('mouseleave', function() {
          this.setAttribute('opacity', '0.9');
          this.removeAttribute('stroke');
          this.removeAttribute('stroke-width');
        });

        bar.addEventListener('click', function() {
          const month = this.getAttribute('data-month');
          const region = this.getAttribute('data-region');
          const value = this.getAttribute('data-value');
          alert(`Szczeg√≥≈Çy:\nMiesiƒÖc: ${month}\nRegion: ${region}\nSprzeda≈º: ${parseInt(value).toLocaleString()} PLN`);
        });
      });
    }

    // Aktualizacja wykresu na podstawie filtra
    function updateChart() {
      const bars = document.querySelectorAll('.bar');
      let visibleData = data;

      if (currentFilter !== 'all') {
        visibleData = data.filter(item => item.region === currentFilter);
      }

      bars.forEach(bar => {
        const region = bar.getAttribute('data-region');
        if (currentFilter === 'all' || region === currentFilter) {
          bar.style.display = 'block';
          bar.setAttribute('opacity', '0.9');
        } else {
          bar.style.display = 'none';
        }
      });

      updateStats(visibleData);
    }

    // Aktualizacja statystyk
    function updateStats(visibleData) {
      const total = visibleData.reduce((sum, item) => sum + item.sales, 0);
      const avg = visibleData.length > 0 ? total / visibleData.length : 0;
      const maxItem = visibleData.reduce((max, item) =>
        item.sales > max.sales ? item : max, visibleData[0] || {sales: 0});

      const northTotal = visibleData.filter(item => item.region === 'P√≥≈Çnoc')
        .reduce((sum, item) => sum + item.sales, 0);
      const southTotal = visibleData.filter(item => item.region === 'Po≈Çudnie')
        .reduce((sum, item) => sum + item.sales, 0);

      const northPercent = total > 0 ? Math.round((northTotal / total) * 100) : 0;
      const southPercent = 100 - northPercent;

      // Aktualizuj tekst statystyk
      const statsDisplay = document.getElementById('statsDisplay');
      if (statsDisplay) {
        const texts = statsDisplay.querySelectorAll('text');
        if (texts[0]) {
          texts[0].innerHTML = `<tspan x="520" dy="0">≈ÅƒÖczna sprzeda≈º: </tspan><tspan font-weight="bold" fill="#333">${total.toLocaleString()} PLN</tspan>`;
        }
        if (texts[1]) {
          texts[1].innerHTML = `<tspan x="520" dy="0">≈örednia miesiƒôczna: </tspan><tspan font-weight="bold" fill="#333">${Math.round(avg).toLocaleString()} PLN</tspan>`;
        }
        if (texts[2] && maxItem) {
          texts[2].innerHTML = `<tspan x="520" dy="0">Najlepszy miesiƒÖc: </tspan><tspan font-weight="bold" fill="#3498db">${maxItem.month} (${maxItem.region}) - ${maxItem.sales.toLocaleString()} PLN</tspan>`;
        }
        if (texts[5]) {
          texts[5].textContent = `${northPercent}% | ${southPercent}%`;
        }
      }
    }

    // Pod≈õwietlenie aktywnego przycisku
    function highlightButton(activeButton) {
      const buttons = document.querySelectorAll('.button[data-region]');
      buttons.forEach(button => {
        button.setAttribute('stroke', 'none');
        button.setAttribute('stroke-width', '0');
      });

      activeButton.setAttribute('stroke', '#333');
      activeButton.setAttribute('stroke-width', '3');
    }

    // Eksport danych
    function exportData() {
      const visibleData = currentFilter === 'all' ?
        data :
        data.filter(item => item.region === currentFilter);

      let csv = 'MiesiƒÖc,Sprzeda≈º,Region\n';
      visibleData.forEach(item => {
        csv += `${item.month},${item.sales},${item.region}\n`;
      });

      // Tworzenie linku do pobrania (symulacja)
      alert(`Eksport CSV (${visibleData.length} rekord√≥w):\n\n${csv}`);
    }

    // Poka≈º informacje o dokumencie
    function showInfo() {
      const metadata = document.querySelector('metadata data-store');
      const jsonData = JSON.parse(metadata.textContent);

      alert(`Informacje o dokumencie SVG:

Tytu≈Ç: ${jsonData.metadata.title}
Wersja: ${jsonData.metadata.version}
Data utworzenia: ${jsonData.metadata.created}
Format danych: ${jsonData.metadata.format}
Waluta: ${jsonData.metadata.currency}
Liczba rekord√≥w: ${jsonData.data.length}
Rozmiar pliku: ~12KB
Format: SVG 1.1 z osadzonymi danymi JSON
Kompatybilno≈õƒá: PrzeglƒÖdarki, edytory graficzne, systemy operacyjne`);
    }

    // Uruchom inicjalizacjƒô
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', init);
    } else {
      init();
    }
